import { Recipe } from './recipe.model';
import { EventEmitter, Injectable } from '@angular/core';
import { Ingredient } from '../shared/ingrediant.model';
import { ShoppingListService } from '../shopping-list/shopping-list-service';

@Injectable()
export class RecipeService{

    recipeSelected = new EventEmitter<Recipe>();

    constructor(private shoppingListService:ShoppingListService){}

    getRecipe(){
        return this.recipes.slice();
    }

    addIngredientsToShoppingList(ingredients:Ingredient[]){
        this.shoppingListService.concatIngredients(ingredients);
    }

    getRecipeDetail(id:number){
        return this.recipes[id];
    }

    private recipes:Recipe[] = [new Recipe('A veg recipe','this is simply a tasty recipe','https://www.momontimeout.com/wp-content/uploads/2018/11/chicken-stir-fry.jpg',[new Ingredient('broccoli',5),new Ingredient('tomato', 50),new Ingredient('potato',10)] ),




    new Recipe('Big Fat Burger','Chicken burger Recipe','data:image/jpeg;base64,',[new Ingredient('buns', 4), new Ingredient('meat', 1)]),
                        
    new Recipe('A Pasta recipe','white sause pasta recipe','data:image/jpeg;base64,',[new Ingredient('pasta',20),new Ingredient('sause', 3)] ),

    new Recipe('Chicken kebab','Mouth-watering recipe','https://cookinglsl.com/wp-content/uploads/2019/07/bacon-wrapped-chicken-kebabs-15-1.jpg',[new Ingredient('chicken',1),new Ingredient('kebab masala', 2),new Ingredient('curd',2)] ),

    new Recipe('PIZZA','Hot Pizza','https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/cheesy-pepperoni-pizza-royalty-free-image-1570815711.jpg?resize=480:*',[new Ingredient('bread',4),new Ingredient('tomato', 10),new Ingredient('cheese',5)] )
    ];

    

}